  const vivus = new Vivus('svg-logo', {
    type: 'oneByOne',
    duration: 300,
    animTimingFunction: Vivus.EASE,
  })
  vivus.play(
    () => {
      if (gRef.value) {
        gRef.value.childNodes.forEach((child, index) => {
          if (child instanceof SVGPathElement) {
            child.style.transition = `all 0.1s ${index * 0.05}s`
            child.style.filter = 'drop-shadow(0 0 10px rgba(255, 255, 255, 0.5))'
          }
        })
      }
      fillOpacity.value = 1
    },
  )
  gsap.from('#letter', {
    opacity: 0,
    y: 50,
    stagger: 0.1,
    ease: 'power1.out',
  })
})
</script>

